function displayTime(time){let now=new Date(Date.now());let date=new Date(Number(time.replace(/\D/g,'')));let seconds=Math.abs((now.getTime()-date.getTime())/1000);if(seconds<60){return Math.round(seconds)+" giây trước";}
let minutes=seconds/60;if(minutes<60){return Math.round(minutes)+" phút trước";}
let hours=Math.abs(now-date)/36e5;if(hours<24){return Math.round(hours)+" giờ trước";}
let options={hour:'2-digit',minute:'2-digit'};return date.toLocaleDateString('en-GB',options).replace(',',"");}
